/*
using System;
using System.Linq;
using System.Collections.Generic;
*/

Dictionary<String,Boolean> tests = new Dictionary<string,bool>
{
    { "one", true },
    { "two", false },
    { "three", true },
    { "four", false },
    { "five", true },
    { "six", true }
};

var testData = from t in tests

    where t.Value == false

    select t;

foreach ( var item in testData )
{
    Console.WriteLine( item.Key );
}

/*
    Output:

        two
        four
*/
